#include <iostream>
#include <numeric>

long long gcd(long long a, long long b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

int main() {
    long long A, B, C, D;
    std::cin >> A >> B >> C >> D;
    long long dx = std::abs(C - A);
    long long dy = std::abs(D - B);
    std::cout << dx + dy - gcd(dx, dy) + 1 << std::endl;
    return 0;
}
