def f(n):
    if n <= 2:
        return 0
    ans = 0
    for i in range(1, n // 2 + 1):
        for j in range(i + 1, n - i):
            k = n - i - j
            ans = max(ans, 1 + f(max(min(i, j), min(j, k), min(i, k))))
    return ans

