#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, Q;
    scanf("%d %d", &N, &Q);
    int A[Q], B[Q];
    for (int i = 0; i < Q; i++) {
        scanf("%d %d", &A[i], &B[i]);
    }
    int min_pos = 1, max_pos = 1;
    int ball_pos = 1;
    for (int i = 0; i < Q; i++) {
        if (ball_pos == A[i]) {
            ball_pos = B[i];
        } else if (ball_pos == B[i]) {
            ball_pos = A[i];
        }
        min_pos = (ball_pos < min_pos) ? ball_pos : min_pos;
        max_pos = (ball_pos > max_pos) ? ball_pos : max_pos;

    }
    int ans = max_pos - min_pos + 1;
    printf("%d\n", ans);
    return 0;
}
