import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int q = sc.nextInt();
        int[] ab = new int[2 * q];
        for (int i = 0; i < 2 * q; i++) {
            ab[i] = sc.nextInt();
        }
        Set<Integer> set = new HashSet<>();
        set.add(1);
        for (int i = 0; i < q; i++) {
            int a = ab[2 * i];
            int b = ab[2 * i + 1];
            Set<Integer> nextSet = new HashSet<>();
            for (int x : set) {
                if (x == a) {
                    nextSet.add(b);
                } else if (x == b) {
                    nextSet.add(a);
                } else {
                    nextSet.add(x);
                }
            }
            set = nextSet;
        }
        int min = Integer.MAX_VALUE;
        int max = Integer.MIN_VALUE;
        for (int x : set) {
            min = Math.min(min, x);
            max = Math.max(max, x);
        }
        System.out.println(max - min + 1);
    }
}
