n, q = map(int, input().split())
ops = []
for _ in range(q):
    ops.append(list(map(int, input().split())))

possible_positions = {1}

for i in range(q):
    new_possible_positions = set()
    for pos in possible_positions:
        a, b = ops[i]
        if pos == a:
            new_possible_positions.add(b)
        elif pos == b:
            new_possible_positions.add(a)
        else:
            new_possible_positions.add(pos)
    possible_positions = new_possible_positions

min_pos = min(possible_positions)
max_pos = max(possible_positions)

print(max_pos - min_pos + 1)

