#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    long long a[N], b[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &a[i], &b[i]);
    }
    int count = 0;
    long long time = 0;
    for (int i = 0; i < N; i++) {
        time += a[i];
        long long wake_time = time % 86400;
        long long end_time = (time + b[i]) % 86400;
        if (wake_time < 10800 && end_time >= 10800) {
            count++;

        } else if (wake_time >=10800 && end_time >= 10800 && wake_time < 21600) {
            if (end_time >=21600){
                count++;
            }
        } else if (wake_time < 10800 && end_time < 10800){
            count++;
        }


    }
    printf("%d\n", count);
    return 0;
}
