#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n), b(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i] >> b[i];
  }

  int max_early_wakeups = 0;
  for (long long start_time = 0; start_time < 86400; ++start_time) {
    int early_wakeups = 0;
    long long current_time = start_time;
    for (int i = 0; i < n; ++i) {
      current_time += a[i];
      if (current_time >= 4 * 3600 && current_time < 7 * 3600) {
        early_wakeups++;
      }
      current_time += b[i];
    }
    max_early_wakeups = max(max_early_wakeups, early_wakeups);
  }

  cout << max_early_wakeups << endl;
  return 0;
}
