import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        long[] b = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
            b[i] = sc.nextLong();
        }
        int ans = 0;
        for (long start = 0; start < 86400; start++) {
            int cnt = 0;
            long cur = start;
            for (int i = 0; i < n; i++) {
                cur += a[i];
                if (cur >= 4 * 3600 && cur < 7 * 3600) {
                    cnt++;
                }
                cur += b[i];
            }
            ans = Math.max(ans, cnt);
        }
        System.out.println(ans);
    }
}
