#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<int> b(n);
  for (int i = 0; i < n; ++i) {
    cin >> b[i];
  }

  int q;
  cin >> q;

  vector<pair<int, int>> ops(q);
  for (int i = 0; i < q; ++i) {
    cin >> ops[i].first >> ops[i].second;
    ops[i].first--; 
    ops[i].second--; 
  }

  int min_hamming = -1;

  for (int i = 0; i < (1 << q); ++i) {
    vector<int> a(n, 0);
    for (int j = 0; j < q; ++j) {
      if ((i >> j) & 1) {
        for (int k = ops[j].first; k <= ops[j].second; ++k) {
          a[k] = 1;
        }
      }
    }

    int hamming = 0;
    for (int j = 0; j < n; ++j) {
      if (a[j] != b[j]) {
        hamming++;
      }
    }

    if (min_hamming == -1 || hamming < min_hamming) {
      min_hamming = hamming;
    }
  }

  cout << min_hamming << endl;
  return 0;
}
