#include <stdio.h>

long long power(long long base, long long exp, long long mod) {
    long long res = 1;
    base %= mod;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        exp /= 2;
    }
    return res;
}

int main() {
    int q;
    scanf("%d", &q);
    while (q--) {
        long long a, m;
        scanf("%lld %lld", &a, &m);
        long long ans = -1;
        for (long long k = 1; k <= 1000; ++k) {
            if (power(a, k, m) == k % m) {
                ans = k;
                break;
            }
        }
        printf("%lld\n", ans);
    }
    return 0;
}
