n = int(input())

if n == 2:
    print(-1)
else:
    result = []
    for i in range(1, n + 1):
        row = []
        for j in range(1, n + 1):
            if i != j:
                row.append(j)
        result.append(row)

    valid = True
    for i in range(n):
        for j in range(n - 1):
            if result[i][j] == result[result[i][j]-1][j]:
                valid = False
                break
        if not valid:
            break

    if valid:
        for row in result:
            print(*row)
    else:
        print(-1)

