#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int n;
int p[MAXN], q[MAXN];
int adj[MAXN][2];
int visited[MAXN];
int max_m = 0;

void dfs(int u, int* path, int len) {
    visited[u] = 1;
    path[len] = u;
    max_m = (max_m > len) ? max_m : len;

    for (int v = 1; v <= n; v++) {
        if (!visited[v]) {
            int valid = 1;
            int cur = u;
            int prev = -1;
            while (cur != v) {
                int next = -1;
                for (int i = 0; i < 2; i++) {
                    if (adj[cur][i] != prev && adj[cur][i] != 0) {
                        next = adj[cur][i];
                        break;
                    }
                }
                if (next == 0) {
                    valid = 0;
                    break;
                }
                for (int i = 0; i < len; i++) {
                    if (path[i] == next) {
                        valid = 0;
                        break;
                    }
                }
                if (!valid) break;
                prev = cur;
                cur = next;
            }
            if (valid) dfs(v, path, len + 1);
        }
    }
    visited[u] = 0;
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n - 1; i++) {
        scanf("%d %d", &p[i], &q[i]);
        adj[p[i]][(adj[p[i]][0] == 0)] = q[i];
        adj[q[i]][(adj[q[i]][0] == 0)] = p[i];
    }

    int path[MAXN];
    dfs(1, path, 1);
    printf("%d\n", max_m);
    return 0;
}
