import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> adj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            adj.get(u).add(v);
            adj.get(v).add(u);
        }
        int max_m = 0;
        for (int i = 0; i < (1 << n); i++) {
            List<Integer> subset = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    subset.add(j);
                }
            }
            if (subset.size() <= max_m) continue;
            boolean ok = true;
            if (subset.size() > 1) {
                for (int j = 0; j < subset.size() - 1; j++) {
                    int u = subset.get(j);
                    int v = subset.get(j + 1);
                    Queue<Integer> q = new LinkedList<>();
                    q.add(u);
                    Set<Integer> visited = new HashSet<>();
                    visited.add(u);
                    List<Integer> path = new ArrayList<>();
                    path.add(u);
                    boolean found = false;
                    while (!q.isEmpty()) {
                        int curr = q.poll();
                        if (curr == v) {
                            found = true;
                            break;
                        }
                        for (int next : adj.get(curr)) {
                            if (!visited.contains(next)) {
                                visited.add(next);
                                q.add(next);
                                path.add(next);
                            }
                        }
                    }
                    if (!found) {
                        ok = false;
                        break;
                    }
                    for (int k = 0; k < path.size(); k++) {
                        int node = path.get(k);
                        if (node != u && node != v && subset.contains(node)) {
                            ok = false;
                            break;
                        }
                    }
                    if (!ok) break;
                }
            }
            if (ok) {
                max_m = Math.max(max_m, subset.size());
            }
        }
        System.out.println(max_m);
    }
}
