#include <stdio.h>
#include <stdlib.h>

int main() {
    long long n, m, k;
    scanf("%lld %lld %lld", &n, &m, &k);
    long long a[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long dp[k + 1][n + 1];
    for (long long i = 0; i <= k; i++) {
        for (long long j = 0; j <= n; j++) {
            dp[i][j] = -1;
        }
    }
    dp[0][0] = 0;
    for (long long i = 1; i <= k; i++) {
        for (long long j = 1; j <= n; j++) {
            for (long long p = 1; p <= m; p++) {
                if (j - p >= 0 && dp[i - 1][j - p] != -1) {
                    if (dp[i][j] == -1 || dp[i][j] < dp[i - 1][j - p] + i * a[j - 1]) {
                        dp[i][j] = dp[i - 1][j - p] + i * a[j - 1];
                    }
                }
            }
        }
    }
    long long ans = 0;
    for (long long i = 1; i <= n; i++) {
        if (dp[k][i] > ans) {
            ans = dp[k][i];
        }
    }
    printf("%lld\n", ans);
    return 0;
}

