#include <iostream>

long long power(long long base, long long exp, long long mod) {
    long long res = 1;
    base %= mod;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        exp /= 2;
    }
    return res;
}

int main() {
    int Q;
    std::cin >> Q;
    while (Q--) {
        long long A, M;
        std::cin >> A >> M;
        long long ans = -1;
        for (long long K = 1; K <= 1000; ++K) {
            if (power(A, K, M) == K % M) {
                ans = K;
                break;
            }
        }

        if (ans == -1 && A == 0 && M > 0) {
            ans = 0;
        }

        if (ans == -1 && A == 1 && M > 0) {
            ans = 1;
        }

        std::cout << ans << std::endl;
    }
    return 0;
}
