#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, m, k;
  cin >> n >> m >> k;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long max_score = 0;
  vector<long long> p(k);
  vector<long long> scores;

  function<void(int)> solve = [&](int index) {
    if (index == k) {
      long long current_score = 0;
      for (int i = 0; i < k; ++i) {
        current_score += (i + 1) * a[p[i]];
      }
      max_score = max(max_score, current_score);
      return;
    }

    for (int i = 0; i < n; ++i) {
      bool valid = true;
      if (index > 0) {
        if (abs(i - p[index - 1]) > m || abs(i - p[index-1]) < 1) {
          valid = false;
        }
      }
      if (valid) {
        p[index] = i;
        solve(index + 1);
      }
    }
  };

  solve(0);
  cout << max_score << endl;
  return 0;
}
