import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int k = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }

        long maxScore = 0;
        long[][] dp = new long[k + 1][n + 1];
        for (int i = 0; i <= n; i++) {
            Arrays.fill(dp[0], 0);
        }

        for (int i = 1; i <= k; i++) {
            for (int j = 1; j <= n; j++) {
                for (int p = Math.max(0, j - m); p < j; p++) {
                    dp[i][j] = Math.max(dp[i][j], dp[i - 1][p] + i * a[j - 1]);
                }
            }
        }

        for (int i = 1; i <= n; i++) {
            maxScore = Math.max(maxScore, dp[k][i]);
        }

        System.out.println(maxScore);
        sc.close();
    }
}
