#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    int n;
    scanf("%d", &n);
    int *a = (int *)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    char s[n];
    scanf("%s", s);
    int *b;
    for (int i = 0; i < n - 1; i++) {
        b = (int *)malloc((n - 1 - i) * sizeof(int));
        if (s[i] == 'M') {
            for (int j = 0; j < n - 1 - i; j++) {
                b[j] = (a[j] > a[j + 1]) ? a[j] : a[j + 1];
            }
        } else {
            for (int j = 0; j < n - 1 - i; j++) {
                b[j] = (a[j] < a[j + 1]) ? a[j] : a[j + 1];
            }
        }
        free(a);
        a = b;
        n--;
    }
    printf("%d\n", a[0]);
    free(a);
    return 0;
}
