import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        String s = sc.next();
        for (int i = 0; i < n - 1; i++) {
            int[] b = new int[n - 1 - i];
            if (s.charAt(i) == 'M') {
                for (int j = 0; j < n - 1 - i; j++) {
                    b[j] = Math.max(a[j], a[j + 1]);
                }
            } else {
                for (int j = 0; j < n - 1 - i; j++) {
                    b[j] = Math.min(a[j], a[j + 1]);
                }
            }
            a = b;
        }
        System.out.println(a[0]);
    }
}
