#include <stdio.h>

int main() {
    double x, p;
    scanf("%lf %lf", &x, &p);
    double prob = p / 100.0;
    double ans = 0;
    if (prob == 1.0) {
        ans = x * 2.0;
    } else if (prob == 0.0) {
        ans = x * 2.0;
    } else {
        ans = (2.0 * x) / (1.0 - prob);
    }

    printf("%.7lf\n", ans);
    return 0;
}
