#include <iostream>
#include <vector>
#include <iomanip>

using namespace std;

int main() {
  long long x;
  double p;
  cin >> x >> p;

  double p_prob = p / 100.0;
  double q_prob = 1.0 - p_prob;

  if (x == 0) {
    cout << fixed << setprecision(10) << 0.0 << endl;
    return 0;
  }

  if (p == 100) {
    cout << fixed << setprecision(10) << (double)x + 1 << endl;
      return 0;
  }

  vector<double> dp(x + 1, 1e18);
  dp[0] = 0;

  for (long long i = 1; i <= x; ++i) {
    dp[i] = min({dp[i], dp[i-1] + 1 + q_prob});
      if (i > 0) dp[i] = min(dp[i], dp[i-1] + 1 + p_prob * (1 + dp[i] )/ (1 - p_prob));

    if (i < x) {
      dp[i] = min(dp[i], dp[i + 1] + 1 + q_prob);
      if (i < x) dp[i] = min(dp[i], dp[i+1] + 1 + p_prob * (1 + dp[i]) / (1- p_prob));

    }

  }


  cout << fixed << setprecision(10) << dp[x] << endl;
  return 0;
}
