import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long x = sc.nextLong();
        double p = sc.nextDouble();
        double ans = 0;
        if (p == 100) {
            ans = (double) (2 * x);
        } else {
            double prob = p / 100.0;
            double q = 1 - prob;
            double E = 0;
            for (long i = 1; i <= x; i++) {
                E += Math.pow(q, i - 1) * (2 * i - 1) * (prob);
            }
            ans = E;


            double dp[][] = new double[(int)x + 1][(int)x + 1];
            for(int i = 0; i <= x; i++){
                Arrays.fill(dp[i], 1e18);
            }
            dp[0][0] = 0;
            for(int i = 0; i <= x; i++){
                for(int j = 0; j <= x; j++){
                    if(dp[i][j] >= 1e18) continue;
                    if(i == j) continue;

                    double next_val = dp[i][j] + 1;

                    if(i > 0){
                        double next_prob = prob * (j+1 <= x ? dp[i-1][j+1] : 1e18) + (1-prob) * (j-1 >=0 ? dp[i-1][j-1] : 1e18);
                        dp[i-1][j] = Math.min(dp[i-1][j], next_prob);
                    }
                    if(j+1 <= x){
                         double next_prob = prob * (j+1 <= x ? dp[i][j+1] : 1e18) + (1-prob) * (j-1 >=0 ? dp[i][j-1] : 1e18);
                         dp[i][j+1] = Math.min(dp[i][j+1], next_prob);
                    }
                    if(i+1 <= x){
                        double next_prob = prob * (j+1 <= x ? dp[i+1][j+1] : 1e18) + (1-prob) * (j-1 >=0 ? dp[i+1][j-1] : 1e18);
                        dp[i+1][j] = Math.min(dp[i+1][j], next_prob);
                    }
                }
            }
            ans = dp[0][(int)x];
        }
        System.out.printf("%.7f\n", ans);
    }
}
