x = int(input())
p = int(input())

dp = {}
def solve(a, b):
    if (a, b) in dp:
        return dp[(a, b)]
    if a == b:
        return 0
    ans = float('inf')
    for next_a in [a - 1, a, a + 1]:
        if next_a < 0:
            continue
        expected_turns = 1 + (p / 100) * solve(next_a, b - 1) + ((100 - p) / 100) * solve(next_a, b + 1)
        ans = min(ans, expected_turns)
    dp[(a, b)] = ans
    return ans

print(solve(0, x))
