import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int Q = sc.nextInt();
        for (int i = 0; i < Q; i++) {
            long A = sc.nextLong();
            long M = sc.nextLong();
            long ans = -1;
            for (long K = 1; K <= 2000; K++) {
                long res = 1;
                long a = A;
                long k = K;
                while (k > 0) {
                    if (k % 2 == 1) res = (res * a) % M;
                    a = (a * a) % M;
                    k /= 2;
                }
                if (res == K) {
                    ans = K;
                    break;
                }
            }
            System.out.println(ans);
        }
    }
}
