#include <stdio.h>
#include <stdlib.h>

long long* cast_spell(long long* arr, int m) {
    long long* res = (long long*)malloc(m * sizeof(long long));
    res[0] = arr[0];
    for (int i = 1; i < m; i++) {
        res[i] = res[i - 1] + arr[i];
    }
    return res;
}

int compare(long long* a, long long* b, int m) {
    for (int i = 0; i < m; i++) {
        if (a[i] < b[i]) return -1;
        if (a[i] > b[i]) return 1;
    }
    return 0;
}

