def solve():
    n, m = map(int, input().split())
    a = []
    for _ in range(n):
        a.append(list(map(int, input().split())))

    def cast_spell(arr):
        s = [0] * m
        s[0] = arr[0]
        for i in range(1, m):
            s[i] = s[i-1] + arr[i]
        return s

    def is_sorted(arrays):
        for i in range(len(arrays) - 1):
            if not (arrays[i] < arrays[i+1]):
                return False
        return True


    q = [(a,0)]
    visited = {tuple(map(tuple,a))}

    while q:
        curr_a, cost = q.pop(0)
        if is_sorted(curr_a):
            print(cost)
            return

        for i in range(n):
            new_a = [list(x) for x in curr_a]
            new_a[i] = cast_spell(new_a[i])

            if tuple(map(tuple,new_a)) not in visited:
                visited.add(tuple(map(tuple,new_a)))
                q.append((new_a, cost + 1))

    print(-1)

solve()
