#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define INF LLONG_MAX

typedef long long ll;

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    ll edges[m][3];
    for (int i = 0; i < m; i++) {
        scanf("%lld %lld %lld", &edges[i][0], &edges[i][1], &edges[i][2]);
        edges[i][0]--; 
        edges[i][1]--; 
    }

    int q;
    scanf("%d", &q);

    for (int i = 0; i < q; i++) {
        int s, t;
        scanf("%d %d", &s, &t);
        s--; 
        t--; 

        ll min_weight = INF;
        for (int j = 0; j < (1 << m); j++) {
            ll current_weight = 0;
            int parent[n];
            for (int k = 0; k < n; k++) parent[k] = k;

            int num_edges = 0;
            for (int k = 0; k < m; k++) {
                if ((j >> k) & 1) {
                    current_weight += edges[k][2];
                    num_edges++;
                    int root_a = edges[k][0];
                    int root_b = edges[k][1];

                    while (parent[root_a] != root_a) root_a = parent[root_a];
                    while (parent[root_b] != root_b) root_b = parent[root_b];

                    if (root_a != root_b) parent[root_a] = root_b;
                }
            }

            int root_s = s;
            while (parent[root_s] != root_s) root_s = parent[root_s];
            int root_t = t;
            while (parent[root_t] != root_t) root_t = parent[root_t];

            int connected = 1;
            for (int k = 0; k < n; k++) {
                int root_k = k;
                while (parent[root_k] != root_k) root_k = parent[root_k];
                if (root_k != root_s && root_k != root_t) {
                    connected = 0;
                    break;
                }
            }

            if (connected) {
                min_weight = (min_weight < current_weight) ? min_weight : current_weight;
            }
        }
        printf("%lld\n", min_weight);
    }

    return 0;
}
