#include <iostream>
#include <vector>
#include <limits>

using namespace std;

int main() {
  int n, m;
  cin >> n >> m;

  vector<tuple<int, int, long long>> edges;
  for (int i = 0; i < m; ++i) {
    int u, v;
    long long w;
    cin >> u >> v >> w;
    edges.emplace_back(u, v, w);
  }

  int q;
  cin >> q;

  for (int i = 0; i < q; ++i) {
    int s, t;
    cin >> s >> t;

    long long min_weight = numeric_limits<long long>::max();
    for (int j = 0; j < (1 << m); ++j) {
      long long current_weight = 0;
      vector<tuple<int, int, long long>> current_edges;
      for (int k = 0; k < m; ++k) {
        if ((j >> k) & 1) {
          current_weight += get<2>(edges[k]);
          current_edges.push_back(edges[k]);
        }
      }

      vector<bool> visited(n + 1, false);
      vector<int> q;
      q.push_back(s);
      visited[s] = true;
      int head = 0;
      while (head < q.size()) {
        int u = q[head++];
        for (const auto& edge : current_edges) {
          if (get<0>(edge) == u && !visited[get<1>(edge)]) {
            visited[get<1>(edge)] = true;
            q.push_back(get<1>(edge));
          } else if (get<1>(edge) == u && !visited[get<0>(edge)]) {
            visited[get<0>(edge)] = true;
            q.push_back(get<0>(edge));
          }
        }
      }

      bool reachable = true;
      for (int k = 1; k <= n; ++k) {
          bool reached_from_s_or_t = false;
          vector<bool> vis(n+1, false);
          vector<int> qu;
          qu.push_back(s);
          vis[s] = true;
          int head2 = 0;
          while(head2 < qu.size()){
              int u = qu[head2++];
              if(u == k) reached_from_s_or_t = true;
              for(auto& e : current_edges){
                  if(get<0>(e) == u && !vis[get<1>(e)]){
                      vis[get<1>(e)] = true;
                      qu.push_back(get<1>(e));
                  } else if(get<1>(e) == u && !vis[get<0>(e)]){
                      vis[get<0>(e)] = true;
                      qu.push_back(get<0>(e));
                  }
              }
          }

          if(!reached_from_s_or_t){
              vector<bool> vis2(n+1, false);
              vector<int> qu2;
              qu2.push_back(t);
              vis2[t] = true;
              int head3 = 0;
              while(head3 < qu2.size()){
                  int u = qu2[head3++];
                  if(u == k) reached_from_s_or_t = true;
                  for(auto& e : current_edges){
                      if(get<0>(e) == u && !vis2[get<1>(e)]){
                          vis2[get<1>(e)] = true;
                          qu2.push_back(get<1>(e));
                      } else if(get<1>(e) == u && !vis2[get<0>(e)]){
                          vis2[get<0>(e)] = true;
                          qu2.push_back(get<0>(e));
                      }
                  }
              }
          }
          if(!reached_from_s_or_t) reachable = false;
      }

      if (reachable) {
        min_weight = min(min_weight, current_weight);
      }
    }
    cout << min_weight << endl;
  }

  return 0;
}
