import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<int[]> edges = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            edges.add(new int[]{sc.nextInt(), sc.nextInt(), sc.nextInt()});
        }
        int q = sc.nextInt();
        for (int i = 0; i < q; i++) {
            int s = sc.nextInt();
            int t = sc.nextInt();
            long minWeight = Long.MAX_VALUE;
            for (int j = 0; j < (1 << m); j++) {
                long currentWeight = 0;
                Set<Integer> vertices = new HashSet<>();
                List<int[]> currentEdges = new ArrayList<>();
                for (int k = 0; k < m; k++) {
                    if ((j >> k) % 2 == 1) {
                        currentWeight += edges.get(k)[2];
                        currentEdges.add(edges.get(k));
                        vertices.add(edges.get(k)[0]);
                        vertices.add(edges.get(k)[1]);
                    }
                }
                if (vertices.size() == 0) continue;
                Set<Integer> reachable = new HashSet<>();
                reachable.add(s);
                Queue<Integer> q1 = new LinkedList<>();
                q1.offer(s);
                while (!q1.isEmpty()) {
                    int u = q1.poll();
                    for (int[] edge : currentEdges) {
                        if (edge[0] == u && !reachable.contains(edge[1])) {
                            reachable.add(edge[1]);
                            q1.offer(edge[1]);
                        } else if (edge[1] == u && !reachable.contains(edge[0])) {
                            reachable.add(edge[0]);
                            q1.offer(edge[0]);
                        }
                    }
                }

                Set<Integer> reachable2 = new HashSet<>(reachable);
                q1 = new LinkedList<>();
                q1.offer(t);
                reachable2.add(t);
                while(!q1.isEmpty()){
                    int u = q1.poll();
                    for(int[] edge : currentEdges){
                        if(edge[0] == u && !reachable2.contains(edge[1])){
                            reachable2.add(edge[1]);
                            q1.offer(edge[1]);
                        } else if(edge[1] == u && !reachable2.contains(edge[0])){
                            reachable2.add(edge[0]);
                            q1.offer(edge[0]);
                        }
                    }
                }
                boolean connected = true;
                for(int k=1; k<=n; ++k) if(!reachable2.contains(k)) connected = false;
                if (connected) {
                    minWeight = Math.min(minWeight, currentWeight);
                }
            }
            System.out.println(minWeight);
        }
    }
}
