#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <limits.h>

long long stringToLong(const char *str, int start, int end) {
    long long result = 0;
    for (int i = start; i <= end; ++i) {
        result = result * 10 + (str[i] - '0');
    }
    return result;
}

long long solve() {
    int k;
    char s[100001];
    scanf("%d", &k);
    scanf("%s", s);
    int n = strlen(s);
    long long minMax = LLONG_MAX;

    for (int i = 0; i < (1 << (n - 1)); ++i) {
        int numCommas = 0;
        for (int j = 0; j < n - 1; ++j) {
            if ((i >> j) & 1) {
                numCommas++;
            }
        }
        if (numCommas > k) continue;

        long long maxNum = 0;
        int start = 0;
        for (int j = 0; j < n - 1; ++j) {
            if ((i >> j) & 1) {
                maxNum = (long long)stringToLong(s, start, j) > maxNum ? (long long)stringToLong(s, start, j) : maxNum;
                start = j + 1;
            }
        }
        maxNum = (long long)stringToLong(s, start, n - 1) > maxNum ? (long long)stringToLong(s, start, n - 1) : maxNum;
        minMax = minMax < maxNum ? minMax : maxNum;
    }
    return minMax;
}

int main() {
    printf("%lld\n", solve());
    return 0;
}
