#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

long long stringToLong(const string& s) {
  long long res = 0;
  for (char c : s) {
    res = res * 10 + (c - '0');
  }
  return res;
}

int main() {
  int k;
  string s;
  cin >> k >> s;

  long long minMax = -1;

  for (int i = 0; i < (1 << (s.length() - 1)); ++i) {
    if (__builtin_popcount(i) > k) continue;

    string currentNum;
    vector<long long> nums;
    for (int j = 0; j < s.length(); ++j) {
      currentNum += s[j];
      if ((i >> j) & 1 || j == s.length() - 1) {
        nums.push_back(stringToLong(currentNum));
        currentNum = "";
      }
    }

    long long maxNum = 0;
    for (long long num : nums) {
      maxNum = max(maxNum, num);
    }

    if (minMax == -1 || maxNum < minMax) {
      minMax = maxNum;
    }
  }
  cout << minMax << endl;
  return 0;
}
