import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int k = sc.nextInt();
        String s = sc.next();
        int n = s.length();
        long minMax = Long.MAX_VALUE;
        for (int i = 0; i < (1 << (n - 1)); i++) {
            if (Integer.bitCount(i) > k) continue;
            List<Long> nums = new ArrayList<>();
            String currentNum = "";
            for (int j = 0; j < n; j++) {
                currentNum += s.charAt(j);
                if (j == n - 1 || (i >> j) % 2 == 1) {
                    nums.add(Long.parseLong(currentNum));
                    currentNum = "";
                }
            }
            long maxNum = 0;
            for (long num : nums) {
                maxNum = Math.max(maxNum, num);
            }
            minMax = Math.min(minMax, maxNum);
        }
        System.out.println(minMax);
    }
}
