#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n], b[n];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &a[i], &b[i]);
    }
    int edges = 0;
    int adj[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            adj[i][j] = 0;
        }
    }
    for (int i = 0; i < n; i++) {
        if (a[i] == 0) {
            adj[0][i] = 1;
            edges++;
        }
        if (b[i] == 0) {
            adj[1][i] = 1;
            edges++;
        }
    }
    for (int i = 0; i < n; i++) {
        int dist1 = 0, dist2 = 0;
        int visited[n];
        for (int j = 0; j < n; j++) {
            visited[j] = 0;
        }
        int q1[n], q2[n];
        int head1 = 0, tail1 = 0, head2 = 0, tail2 = 0;
        q1[tail1++] = 0;
        visited[0] = 1;
        while (head1 < tail1) {
            int u = q1[head1++];
            if (u == i) {
                dist1 = visited[u] -1;
                break;
            }
            for (int v = 0; v < n; v++) {
                if (adj[u][v] == 1 && visited[v] == 0) {
                    visited[v] = visited[u] + 1;
                    q1[tail1++] = v;
                }
            }
        }
        for (int j = 0; j < n; j++) {
            visited[j] = 0;
        }
        q2[tail2++] = 1;
        visited[1] = 1;
        while (head2 < tail2) {
            int u = q2[head2++];
            if (u == i) {
                dist2 = visited[u] - 1;
                break;
            }
            for (int v = 0; v < n; v++) {
                if (adj[u][v] == 1 && visited[v] == 0) {
                    visited[v] = visited[u] + 1;
                    q2[tail2++] = v;
                }
            }
        }
        if (dist1 != a[i] || dist2 != b[i]) {
            printf("-1\n");
            return 0;
        }
    }
    printf("%d\n", edges);
    return 0;
}
