import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
            b[i] = sc.nextInt();
        }
        if (a[0] != 0 || b[0] != 0 || a[1] != 1 || b[1] != 0) {
            System.out.println("-1");
            return;
        }
        Set<Integer> set = new HashSet<>();
        for (int i = 2; i < n; i++) {
            if (Math.abs(a[i] - b[i]) > 1 || Math.abs(a[i] - b[i]) < 0 || a[i] < 0 || b[i] < 0) {
                System.out.println("-1");
                return;
            }
            if (a[i] == b[i]) {
                set.add(i);
            }
        }
        int ans = n - 1 + set.size();
        System.out.println(ans);
    }
}
