import heapq

def solve():
    n = int(input())
    ab = []
    for _ in range(n):
        ab.append(list(map(int, input().split())))

    adj = [[] for _ in range(n)]
    edges = 0

    for i in range(n):
        if ab[i][0] == 0 and ab[i][1] == 1:
            continue
        if ab[i][0] == 1 and ab[i][1] == 0:
            continue

        if ab[i][0] + ab[i][1] > n-1 or abs(ab[i][0] - ab[i][1]) > 1:
            print(-1)
            return


    for i in range(n):
        for j in range(i+1,n):
            if abs(ab[i][0] - ab[j][0]) + abs(ab[i][1] - ab[j][1]) == 2 or (ab[i][0] == ab[j][0] and ab[i][1] == ab[j][1] +1) or (ab[i][0] == ab[j][0] +1 and ab[i][1] == ab[j][1]):
                adj[i].append(j)
                adj[j].append(i)
                edges += 1


    if edges < n -1:
        print(-1)
        return


    visited = [False] * n
    q = [0]
    visited[0] = True
    count = 0

    while q:
        u = q.pop(0)
        count += 1
        for v in adj[u]:
            if not visited[v]:
                visited[v] = True
                q.append(v)
    if count != n:
        print(-1)
        return


    print(edges)

