#include <stdio.h>
#include <stdlib.h>
#include <math.h>

long long *generate_subsets(long long *a, int n) {
    int num_subsets = 1 << n;
    long long *subsets = (long long *)malloc(num_subsets * sizeof(long long));
    for (int i = 0; i < num_subsets; i++) {
        subsets[i] = 0;
        for (int j = 0; j < n; j++) {
            if ((i >> j) & 1) {
                subsets[i] += a[j];
            }
        }
    }
    return subsets;
}

int count_coins(long long amount) {
    int count = 0;
    long long power_of_10 = 1;
    while (amount > 0) {
        count += amount % 10;
        amount /= 10;
    }
    return count;
}

int main() {
    int n;
    scanf("%d", &n);
    long long *a = (long long *)malloc(n * sizeof(long long));
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long *subsets = generate_subsets(a, n);
    long long max_sum = 0;
    for (int i = 0; i < (1 << n); i++) {
        if (subsets[i] > max_sum)
            max_sum = subsets[i];
    }
    free(subsets);
    int coins = count_coins(max_sum);
    printf("%d\n", coins);
    free(a);
    return 0;
}
