import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        Set<Long> sums = new HashSet<>();
        for (int i = 0; i < (1 << n); i++) {
            long sum = 0;
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    sum += a[j];
                }
            }
            if (sum > 0) sums.add(sum);
        }
        long maxSum = 0;
        for (long sum : sums) {
            maxSum = Math.max(maxSum, sum);
        }
        int count = 0;
        long temp = maxSum;
        long pow10 = 1;
        while (temp > 0) {
            count += (int) (temp % 10);
            temp /= 10;
            pow10 *= 10;
        }
        System.out.println(count);
    }
}
