#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n], b[n];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &a[i], &b[i]);
    }
    if (a[0] != 0 || b[0] != 0 || a[1] != 1 || b[1] != 0) {
        printf("-1\n");
        return 0;
    }
    int edges = 0;
    int dist1[n], dist2[n];
    for (int i = 0; i < n; i++) {
        dist1[i] = a[i];
        dist2[i] = b[i];
    }
    for (int i = 0; i < n; i++) {
        int d = abs(dist1[i] - dist2[i]);
        if (d % 2 != 0) {
            printf("-1\n");
            return 0;
        }
    }
    for (int i = 0; i < n; i++) {
        edges += dist1[i];
    }
    int count = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            if (abs(dist1[i] - dist1[j]) + abs(dist2[i] - dist2[j]) > 2) {
                count++;
            }
        }
    }
    edges = (edges + 1) / 2 + count;
    printf("%d\n", edges);
    return 0;
}
