import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
            b[i] = sc.nextInt();
        }
        if (a[0] != 0 || b[1] != 0 || a[1] != 1 || b[0] != 1) {
            System.out.println(-1);
            return;
        }
        List<Integer>[] g = new ArrayList[n];
        for (int i = 0; i < n; i++) {
            g[i] = new ArrayList<>();
        }
        for (int i = 0; i < n; i++) {
            if (a[i] == 0 || b[i] == 0) continue;
            for (int j = 0; j < n; j++) {
                if (a[j] == a[i] -1 && b[j] == b[i]) {
                    g[i].add(j);
                    break;
                }
                if (a[j] == a[i] && b[j] == b[i] - 1) {
                    g[i].add(j);
                    break;
                }
            }
        }
        int ans = 0;
        for(int i = 0; i < n; i++){
            ans += g[i].size();
        }
        System.out.println(ans / 2 + n-1);
    }
}
