#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int main() {
    int N;
    scanf("%d", &N);
    long long A[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &A[i]);
    }
    long long max_sum = 0;
    for (int i = 0; i < (1 << N); i++) {
        long long current_sum = 0;
        for (int j = 0; j < N; j++) {
            if ((i >> j) & 1) {
                current_sum += A[j];
            }
        }
        if (current_sum > max_sum) {
            max_sum = current_sum;
        }
    }
    int count = 0;
    long long temp = max_sum;
    while (temp > 0) {
        count++;
        temp /= 10;
    }
    printf("%d\n", count);
    return 0;
}
