#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int i = 1, sum = 0, max_val = 0;
    int *arr = (int *)malloc(sizeof(int) * n);
    int k=0;
    while (sum < n) {
        sum += i;
        if (sum <= n) {
            arr[k++] = i;
            max_val = i;
        }
        i++;
    }
    if (sum > n) {
        sum -= i-1;
        k--;
        i--;
        int diff = sum - n;
        while(diff>0){
            for(int j=k-1; j>=0; j--){
                if(arr[j] > diff){
                    sum -= arr[j];
                    diff = sum -n;
                    arr[j] = 0;
                    k--;
                    break;
                }
            }
        }

    }

    for (int j = 0; j < k; j++) {
        if(arr[j]!=0)
            printf("%d\n", arr[j]);
    }
    free(arr);
    return 0;
}
