#include <iostream>
#include <vector>
#include <numeric>

int main() {
    int n;
    std::cin >> n;
    std::vector<int> result;
    int sum = 0;
    for (int i = 1; sum < n; ++i) {
        if (sum + i <= n) {
            sum += i;
            result.push_back(i);
        }
    }
    if (sum > n) {
        int diff = sum - n;
        for (int i = 0; i < result.size(); ++i) {
            if (result[i] == diff) {
                result.erase(result.begin() + i);
                break;
            }
        }

        int cur = 1;
        int curSum = 0;
        std::vector<int> res;
        while (curSum < n) {
          curSum += cur;
          res.push_back(cur);
          cur++;
        }

        if (curSum > n) {
          int diff2 = curSum - n;
          for (int i = 0; i < res.size(); i++) {
            if (res[i] == diff2) {
              res.erase(res.begin() + i);
              break;
            }
          }
          for (int x : res) std::cout << x << std::endl;
        } else {
            for (int x : result) std::cout << x << std::endl;
        }

    } else {
        for (int x : result) std::cout << x << std::endl;
    }
    return 0;
}
