import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<Integer> ans = solve(n);
        for (int x : ans) {
            System.out.println(x);
        }
    }

    static List<Integer> solve(int n) {
        List<Integer> res = new ArrayList<>();
        int i = 1;
        int sum = 0;
        while (sum < n) {
            sum += i;
            res.add(i);
            i++;
        }
        if (sum == n) return res;
        int diff = sum - n;
        int j = res.size() -1;
        while(diff > 0){
            int val = res.get(j);
            if(val > diff){
                j--;
                continue;
            }
            res.remove(j);
            diff -= val;

        }
        return res;
    }
}
