#include <stdio.h>
#include <stdbool.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);

    int K[N];
    int languages[N][M];
    for (int i = 0; i < N; i++) {
        scanf("%d", &K[i]);
        for (int j = 0; j < K[i]; j++) {
            scanf("%d", &languages[i][j]);
            languages[i][j]--; 
        }
    }

    bool can_communicate[N][N];
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            can_communicate[i][j] = false;
            for (int k = 0; k < K[i]; k++) {
                for (int l = 0; l < K[j]; l++) {
                    if (languages[i][k] == languages[j][l]) {
                        can_communicate[i][j] = true;
                        break;
                    }
                }
                if (can_communicate[i][j]) break;
            }
        }
    }

    for (int k = 0; k < N; k++) {
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                if (i == j) continue;
                if (can_communicate[i][k] && can_communicate[k][j]) {
                    can_communicate[i][j] = true;
                }
            }
        }
    }

    bool all_communicate = true;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            if (i == j) continue;
            if (!can_communicate[i][j]) {
                all_communicate = false;
                break;
            }
        }
        if (!all_communicate) break;
    }

    if (all_communicate) {
        printf("YES\n");
    } else {
        printf("NO\n");
    }

    return 0;
}
