import sys

def solve():
    n, m = map(int, sys.stdin.readline().split())
    participants = []
    for _ in range(n):
        participants.append(list(map(int, sys.stdin.readline().split()))[1:])

    adj = [[False] * n for _ in range(n)]
    for i in range(n):
        for j in range(i + 1, n):
            common_lang = False
            for lang_i in participants[i]:
                if lang_i in participants[j]:
                    common_lang = True
                    break
            adj[i][j] = adj[j][i] = common_lang

    for k in range(n):
        for i in range(n):
            for j in range(n):
                adj[i][j] = adj[i][j] or (adj[i][k] and adj[k][j])

    connected = True
    for i in range(n):
        for j in range(i + 1, n):
            if not adj[i][j]:
                connected = False
                break
        if not connected:
            break

    print("YES" if connected else "NO")

