#include <stdio.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int count[100001] = {0};
    for (int i = 0; i < N; i++) {
        int X;
        scanf("%d", &X);
        count[X]++;
    }
    int pairs = 0;
    for (int i = 1; i <= 100000; i++) {
        pairs += count[i] / 2;
        count[i] %= 2;
    }
    int odd_count[M] = {0};
    for (int i = 1; i <= 100000; i++) {
        if (count[i] == 1) {
            odd_count[i % M]++;
        }
    }
    pairs += odd_count[0] / 2;
    for (int i = 1; i <= M / 2; i++) {
        pairs += (odd_count[i] > 0 && odd_count[M - i] > 0) ? 1 : 0;
        if(odd_count[i] > 0 && odd_count[M - i] > 0) {
            odd_count[i] = 0;
            odd_count[M - i] = 0;
        }
    }
    if (M % 2 == 0 && odd_count[M / 2] > 1)
        pairs += odd_count[M / 2] / 2;

    printf("%d\n", pairs);
    return 0;
}
