import java.util.*;
public class Main{
    public static void main(String[] args){
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        Map<Integer,Integer> map = new HashMap<>();
        for(int i=0;i<n;i++){
            int x = sc.nextInt();
            map.put(x,map.getOrDefault(x,0)+1);
        }
        int ans = 0;
        for(int key:map.keySet()){
            int count = map.get(key);
            ans += count/2;
            map.put(key,count%2);
        }
        int[] cnt = new int[m];
        for(int key:map.keySet()){
            cnt[key%m]++;
        }
        ans += cnt[0]/2;
        for(int i=1;i<=m/2;i++){
            ans += Math.min(cnt[i],cnt[m-i]);
        }
        System.out.println(ans);
    }
}
