#include <iostream>
#include <algorithm>

using namespace std;

int main() {
  long long n, a;
  cin >> n >> a;

  if (n == 1) {
    cout << 1 << endl;
    return 0;
  }

  long long left = 0, right = 2e12;
  long long ans = 2e12;

  while (left <= right) {
    long long mid = left + (right - left) / 2;
    long long cur = 0;
    long long time = 0;
    long long rate = 1;

    if (mid <= a) {
      if (mid >= n) {
        ans = min(ans, mid);
      }
      left = mid + 1;
      continue;
    }

    time = 1;
    cur = 1;

    while (cur < n) {
      long long next_eat = time;
      long long next_time = next_eat + a;
      long long add = (next_time - time) * rate;
      cur += add;
      if (cur >= n) break;
      time = next_time;
      rate = cur;
    }

    if (cur >= n) {
      ans = min(ans, time);
      right = mid - 1;
    } else {
      left = mid + 1;
    }
  }
  cout << ans << endl;
  return 0;
}
