import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long a = sc.nextLong();

        if (n <= a + 1) {
            System.out.println(n);
            return;
        }

        long left = 0;
        long right = 2 * n;
        long ans = n;

        while (left <= right) {
            long mid = left + (right - left) / 2;
            long cookies = 0;
            long time = 0;
            long rate = 1;

            if (mid <= a) {
                cookies = mid;
            } else {
                cookies = a + 1;
                time = a + 1;
                long rem = mid - (a + 1);
                long x = a + 1;
                while (rem > 0) {
                    long bake_time = (long) Math.ceil((double) rem / x);
                    cookies += bake_time * x;
                    time += bake_time;
                    rem = rem - bake_time * x;
                    if(rem > 0){
                         x = cookies;
                         time +=a;
                    }
                }
            }


            if (cookies >= n) {
                ans = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        System.out.println(ans);
    }
}
