#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, m;
  cin >> n >> m;

  long long mod = 1000000007;
  long long count = 0;

  vector<long long> c(m);
  vector<vector<bool>> adj(n, vector<bool>(n, false));
  vector<long long> visited(n,0);
  function<void(long long)> solve = [&](long long k) {
    if (k == m) {
      vector<vector<bool>> adj_matrix(n, vector<bool>(n, false));
      long long current_city = 0;
      for (long long i = 0; i < m; ++i) {
        adj_matrix[current_city][c[i] -1] = true;
        current_city = c[i]-1;
      }

      vector<bool> visited(n, false);
      function<void(long long)> dfs = [&](long long u) {
        visited[u] = true;
        for (long long v = 0; v < n; ++v) {
          if (adj_matrix[u][v] && !visited[v]) {
            dfs(v);
          }
        }
      };

      dfs(0);
      bool connected = true;
      for (long long i = 0; i < n; ++i) {
        if (!visited[i]) {
          connected = false;
          break;
        }
      }
      if (connected) count = (count + 1) % mod;
      return;
    }

    for (long long i = 1; i <= n; ++i) {
      c[k] = i;
      solve(k + 1);
    }
  };

  solve(0);
  cout << count << endl;
  return 0;
}
